function normalizePhone(phoneNumber) {
    // Si el n√∫mero es null, undefined o vac√≠o, retornarlo tal como est√°
    if (!phoneNumber || phoneNumber === '') {
        return phoneNumber;
    }

    // Convertir a string y limpiar: quitar espacios, guiones, par√©ntesis, etc.
    let cleanPhone = phoneNumber.toString().replace(/[^0-9+]/g, '');

    // Casos para n√∫meros mexicanos (+52)

    // Patr√≥n: +521XXXXXXXXXX -> +52XXXXXXXXXX (remover el 1 despu√©s de +52)
    if (/^\+521[0-9]{10}$/.test(cleanPhone)) {
        return '+52' + cleanPhone.substring(4);
    }

    // Patr√≥n: 521XXXXXXXXXX -> +52XXXXXXXXXX (sin +, con 1)
    if (/^521[0-9]{10}$/.test(cleanPhone)) {
        return '+52' + cleanPhone.substring(3);
    }

    // Patr√≥n: +52XXXXXXXXXX -> ya est√° correcto
    if (/^\+52[0-9]{10}$/.test(cleanPhone)) {
        return cleanPhone;
    }

    // Patr√≥n: 52XXXXXXXXXX -> +52XXXXXXXXXX (agregar +)
    if (/^52[0-9]{10}$/.test(cleanPhone)) {
        return '+' + cleanPhone;
    }

    // Patr√≥n: 1XXXXXXXXXX (11 d√≠gitos empezando con 1) -> +52XXXXXXXXXX
    if (/^1[0-9]{10}$/.test(cleanPhone)) {
        return '+52' + cleanPhone.substring(1);
    }

    // Patr√≥n: XXXXXXXXXX (10 d√≠gitos) -> +52XXXXXXXXXX (asumir M√©xico)
    if (/^[0-9]{10}$/.test(cleanPhone)) {
        return '+52' + cleanPhone;
    }

    // Si no coincide con ning√∫n patr√≥n conocido, retornar original
    return phoneNumber;
}

